import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import '../styles/tarefas.css';

const etapas = [
  { nome: 'PROJETO NA FILA', cor: '#cccccc' },
  { nome: 'EM NEGOCIAÇÃO', cor: '#f0ad4e' },
  { nome: 'EXECUÇÃO DO TIME', cor: '#5bc0de' },
  { nome: 'APRESENTAÇÃO AO CLIENTE', cor: '#9370db' },
  { nome: 'VALORES', cor: '#ff69b4' },
  { nome: 'ENTREGA AO CLIENTE', cor: '#5cb85c' },
];

const Tarefas = () => {
  const [tarefas, setTarefas] = useState([]);
  const location = useLocation();

  const params = new URLSearchParams(location.search);
  const quadroId = params.get('quadro');
  const token = localStorage.getItem('access');

  useEffect(() => {
    if (quadroId && token) {
      fetch(`http://localhost:8000/api/tarefas/?quadro=${quadroId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          if (Array.isArray(data)) {
            setTarefas(data);
          } else if (Array.isArray(data.results)) {
            setTarefas(data.results);
          } else {
            setTarefas([]);
          }
        })
        .catch(err => {
          console.error('Erro ao buscar tarefas:', err);
          setTarefas([]);
        });
    }
  }, [quadroId, token]);

  return (
    <div className="kanban-container">
      {etapas.map((etapa, index) => (
        <div key={index} className="kanban-col">
          <div className="kanban-header" style={{ backgroundColor: etapa.cor }}>
            {etapa.nome}
          </div>
          <div className="kanban-cards">
            {tarefas
              .filter(tarefa => tarefa.etapa === etapa.nome)
              .map(tarefa => (
                <div key={tarefa.id} className="kanban-card">
                  <h6>{tarefa.titulo}</h6>
                  <p>{tarefa.descricao}</p>
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Tarefas;
