import React, { useEffect, useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import '../styles/tarefas.css';

const etapas = [
  'PROJETO NA FILA',
  'EM NEGOCIAÇÃO',
  'EXECUÇÃO DO TIME',
  'APRESENTAÇÃO AO CLIENTE',
  'VALORES',
  'ENTREGA AO CLIENTE'
];

const Tarefas = () => {
  const [quadros, setQuadros] = useState([]);
  const token = localStorage.getItem('access');

  useEffect(() => {
    fetch('http://localhost:8000/api/quadros/', {
      headers: {
        Authorization: `Bearer ${token}`,
      }
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setQuadros(data);
      });
  }, [token]);

  const onDragEnd = result => {
    const { destination, source, draggableId } = result;

    if (!destination || destination.droppableId === source.droppableId) return;

    const quadroId = parseInt(draggableId);
    const novaEtapa = destination.droppableId;

    // Atualiza no backend
    fetch(`http://localhost:8000/api/quadros/${quadroId}/`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ situacao: novaEtapa })
    })
      .then(res => res.json())
      .then(data => {
        setQuadros(prev => prev.map(q => q.id === quadroId ? { ...q, situacao: novaEtapa } : q));
      });
  };

  return (
    <div className="kanban-container mt-4">
      <h3 className="mb-4">Quadros por Etapas</h3>
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="row">
          {etapas.map((etapa, idx) => (
            <div key={idx} className="col-md-2 kanban-col">
              <div className="kanban-header">{etapa}</div>
              <Droppable droppableId={etapa}>
                {(provided) => (
                  <div
                    className="kanban-cards"
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                  >
                    {quadros
                      .filter(quadro => quadro.situacao === etapa)
                      .map((quadro, index) => (
                        <Draggable key={quadro.id} draggableId={`${quadro.id}`} index={index}>
                          {(provided) => (
                            <div
                              className="kanban-card"
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                            >
                              <strong>{quadro.nome}</strong>
                              <p className="text-muted small">{quadro.empresa}</p>
                              <p>{quadro.responsavel}</p>
                            </div>
                          )}
                        </Draggable>
                      ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </div>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
};

export default Tarefas;
