import React, { useEffect, useState } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import '../styles/tarefas.css';

const Tarefas = () => {
  const [quadros, setQuadros] = useState([]);
  const [etapas, setEtapas] = useState([]);
  const token = localStorage.getItem('access');

  useEffect(() => {
    fetch('http://localhost:8000/api/quadros/', {
      headers: {
        Authorization: `Bearer ${token}`,
      }
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setQuadros(data);
      });

    fetch(`http://localhost:8000/api/etapas/`, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) setEtapas(data);
      });
  }, [token]);

  const calcularStatus = (dataCriacao) => {
    const data = new Date(dataCriacao);
    const dataLimite = new Date(data);
    dataLimite.setDate(dataLimite.getDate() + 3);
    const hoje = new Date();

    const status = hoje <= dataLimite ? 'Em dia' : 'Atrasado';
    const previsao = dataLimite.toLocaleDateString();
    return { status, previsao };
  };

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (!destination || destination.droppableId === source.droppableId) return;

    const movedQuadro = quadros.find(q => q.id.toString() === draggableId);
    if (!movedQuadro) return;

    const updatedQuadros = quadros.map(quadro =>
      quadro.id.toString() === draggableId
        ? {
            ...quadro,
            situacao: destination.droppableId
          }
        : quadro
    );
    setQuadros(updatedQuadros);

    fetch(`http://localhost:8000/api/quadros/${draggableId}/`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        situacao: destination.droppableId
      }),
    });
  };

  const adicionarEtapa = async () => {
    const nome = prompt("Digite o nome da nova etapa:");
    if (!nome) return;

    const novaEtapa = {
      nome,
      quadro: 1  // ID fixo se voc√™ s√≥ tem um quadro; pode tornar din√¢mico depois
    };

    const res = await fetch('http://localhost:8000/api/etapas/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify(novaEtapa)
    });

    const data = await res.json();

    if (res.ok) {
      setEtapas(prev => [...prev, data]);
    } else {
      alert("Erro ao salvar etapa no backend.");
    }
  };

  const excluirEtapa = async (etapaId) => {
    const confirm = window.confirm("Deseja mesmo excluir esta etapa?");
    if (!confirm) return;

    const res = await fetch(`http://localhost:8000/api/etapas/${etapaId}/`, {
      method: 'DELETE',
      headers: {
        Authorization: `Bearer ${token}`
      }
    });

    if (res.status === 204) {
      setEtapas(prev => prev.filter(e => e.id !== etapaId));
    } else {
      alert("Erro ao excluir etapa.");
    }
  };

  return (
    <div className="kanban-page">
      <div className="kanban-title-wrapper text-center">
        <h3 className="kanban-title">Quadros por Etapas</h3>
        <button className="btn btn-outline-primary mt-2" onClick={adicionarEtapa}>
          ‚ûï Nova Etapa
        </button>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="kanban-container">
          {etapas.map((etapa, idx) => (
            <div key={idx} className="kanban-col">
              <div className="kanban-header d-flex justify-content-between align-items-center">
                <span>{etapa.nome}</span>
                <button className="btn btn-sm btn-danger" onClick={() => excluirEtapa(etapa.id)}>üóëÔ∏è</button>
              </div>
              <Droppable droppableId={etapa.nome}>
                {(provided) => (
                  <div
                    className="kanban-cards"
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                  >
                    {quadros
                      .filter(quadro => quadro.situacao === etapa.nome)
                      .map((quadro, index) => {
                        const { status, previsao } = calcularStatus(quadro.criado_em);
                        return (
                          <Draggable
                            key={quadro.id}
                            draggableId={quadro.id.toString()}
                            index={index}
                          >
                            {(provided) => (
                              <div
                                className="kanban-card"
                                ref={provided.innerRef}
                                {...provided.draggableProps}
                                {...provided.dragHandleProps}
                              >
                                <strong>{quadro.nome}</strong>
                                <p>{quadro.empresa}</p>
                                <p>{quadro.responsavel}</p>
                                <p className={`status ${status === 'Em dia' ? 'success' : 'danger'}`}>
                                  {status === 'Em dia' ? '‚úÖ Em dia' : '‚ùå Atrasado'}
                                </p>
                                <p>Entrega at√©: <strong>{previsao}</strong></p>
                              </div>
                            )}
                          </Draggable>
                        );
                      })}
                    {provided.placeholder}

                    {quadros.filter(quadro => quadro.situacao === etapa.nome).length === 0 && (
                      <div className="kanban-card text-muted small">
                        Sem tarefas
                      </div>
                    )}
                  </div>
                )}
              </Droppable>
            </div>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
};

export default Tarefas;
