import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import Home from './pages/Home';
import Login from './pages/Login';
import Logout from './pages/Logout';
import PrivateRoute from './routes/PrivateRoute';
import Kanban from './pages/Kanban';




const LayoutWrapper = ({ children }) => {
  const location = useLocation();
  const hideHeaderRoutes = ['/login', '/kanban']; // rotas onde o Header não aparece
  const isHeaderHidden = hideHeaderRoutes.includes(location.pathname);

  return (
    <div style={{ display: 'flex' }}>
      {!isHeaderHidden && <Sidebar />}
      <div style={{ marginLeft: isHeaderHidden ? '0' : '6rem', padding: '2rem', width: '100%' }}>
        {!isHeaderHidden && <Header />}
        {children}
      </div>
    </div>
  );
};

function App() {
  return (
    <Router>
      <LayoutWrapper>
        <Routes>
        {/* Login e logout são públicos */}
        <Route path="/login" element={<Login />} />
        <Route path="/logout" element={<Logout />} />
             {/* Rotas privadas protegidas por token */}
          <Route element={<PrivateRoute />}>
          <Route path="/" element={<Home />} />
          <Route path="/home" element={<Home />} />
          <Route path="/kanban" element={<Kanban />} />

          {/* Adicione outras rotas privadas aqui */}
          </Route>

        </Routes>
      </LayoutWrapper>
    </Router>
  );
}

export default App;
