import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import '../styles/sidebar.css';
import logo from '../assets/logocomece.svg';
import { jwtDecode } from 'jwt-decode'; 

const Sidebar = () => {
  const location = useLocation();
  const token = localStorage.getItem('access');

  let username = 'usu치rio';

  console.log('TOKEN:', token);
  if (token) {
    try {
      const decoded = jwtDecode(token);
      console.log('DECODED JWT:', decoded); // 游녣 Veja o que aparece no console
      username = decoded.first_name || decoded.username || 'usu치rio';
    } catch (e) {
      console.error('Erro ao decodificar token:', e);
    }
  }

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <img src={logo} alt="Logo" className="img-fluid" />
      </div>
      <hr />
      <p className="welcome-text">Bem-vindo, {username}!</p>
      <nav className="nav flex-column">
        <Link to="/" className={`nav-link nav-link-beat ${location.pathname === '/' ? 'active' : ''}`}>
          <i className="fas fa-home me-2 fa-lg"></i>
          <span className="sidebar-info">Home</span>
          </Link>
          <Link to="/kanban" className={`nav-link nav-link-beat ${location.pathname === '/' ? 'active' : ''}`}>
          <i className="fas fa-home me-2 fa-lg"></i>
          <span className="sidebar-info">Home</span>
          </Link>
      </nav>
    </div>
  );
};

export default Sidebar;
