import React, { useEffect, useState } from 'react';
import '../styles/kanban.css';

const Kanban = () => {
  const [quadros, setQuadros] = useState([]);
  const [novoQuadro, setNovoQuadro] = useState({
    nome: '',
    responsavel: '',
    ativo: true,
  });

  const token = localStorage.getItem('access');

  useEffect(() => {
    if (!token) return;

    fetch('http://localhost:8000/api/quadros/', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setQuadros(data);
        } else if (Array.isArray(data.results)) {
          setQuadros(data.results);
        } else {
          setQuadros([]);
        }
      })
      .catch(err => {
        console.error('Erro ao buscar quadros:', err);
        setQuadros([]);
      });
  }, [token]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setNovoQuadro({
      ...novoQuadro,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleCreateQuadro = (e) => {
    e.preventDefault();

    const dados = {
      ...novoQuadro,
      etapa: 'PROJETO NA FILA'  // Adiciona a etapa inicial ao criar
    };

    fetch('http://localhost:8000/api/quadros/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify(dados)
    })
      .then(res => res.json())
      .then(data => {
        setQuadros(prev => [...prev, data]);
        setNovoQuadro({ nome: '', responsavel: '', ativo: true });
      });
  };

  const toggleStatus = (id, statusAtual) => {
    fetch(`http://localhost:8000/api/quadros/${id}/`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ ativo: !statusAtual })
    })
      .then(res => res.json())
      .then(data => {
        setQuadros(prev =>
          prev.map(q => (q.id === id ? { ...q, ativo: data.ativo } : q))
        );
      })
      .catch(err => console.error('Erro ao atualizar status:', err));
  };

  const handleDelete = (id) => {
    if (window.confirm('Tem certeza que deseja excluir este quadro?')) {
      fetch(`http://localhost:8000/api/quadros/${id}/`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => {
          if (res.status === 204) {
            setQuadros(prev => prev.filter(q => q.id !== id));
          }
        })
        .catch(err => console.error('Erro ao excluir quadro:', err));
    }
  };

  return (
    <div className="kanban-dashboard container mt-5">
      <h3>Dashboard</h3>

      {/* Formulário */}
      <form onSubmit={handleCreateQuadro} className="mb-4">
        <div className="row g-2">
          <div className="col-md-4">
            <input
              type="text"
              name="nome"
              className="form-control"
              placeholder="Nome do quadro"
              value={novoQuadro.nome}
              onChange={handleChange}
              required
            />
          </div>
          <div className="col-md-4">
            <input
              type="text"
              name="responsavel"
              className="form-control"
              placeholder="Responsável"
              value={novoQuadro.responsavel}
              onChange={handleChange}
              required
            />
          </div>
          <div className="col-md-2 d-flex align-items-center">
            <label className="form-check-label me-2">Ativo:</label>
            <input
              type="checkbox"
              name="ativo"
              className="form-check-input"
              checked={novoQuadro.ativo}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-2">
            <button type="submit" className="btn btn-success w-100">Criar</button>
          </div>
        </div>
      </form>

      {/* Tabela */}
      <table className="table table-bordered table-hover">
        <thead className="table-light">
          <tr>
            <th>Nome do quadro</th>
            <th>Responsável</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(quadros) ? (
            quadros.map(quadro => (
              <tr key={quadro.id}>
                <td>{quadro.nome}</td>
                <td>{quadro.responsavel}</td>
                <td className="d-flex align-items-center gap-2">
                  <span className={`badge ${quadro.ativo ? 'bg-success' : 'bg-danger'}`}>
                    {quadro.ativo ? 'Ativo' : 'Inativo'}
                  </span>

                  <button
                    className="btn btn-sm btn-outline-secondary"
                    onClick={() => toggleStatus(quadro.id, quadro.ativo)}
                    title="Ativar/Inativar"
                  >
                    <i className="fas fa-arrows-rotate"></i>
                  </button>

                  <button
                  className="btn btn-sm btn-outline-primary"
                  onClick={() => window.location.href = `/tarefas`}
                  title="Ver tarefas"
                >
                  <i className="fas fa-eye"></i>
                </button>

                  <button
                    className="btn btn-sm btn-outline-danger"
                    onClick={() => handleDelete(quadro.id)}
                    title="Excluir quadro"
                  >
                    <i className="fas fa-trash-alt"></i>
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">Nenhum quadro disponível.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Kanban;
