  { nome: 'PROJETO NA FILA', cor: '#cccccc' },
  { nome: 'EM NEGOCIAÇÃO', cor: '#f0ad4e' },
  { nome: 'EXECUÇÃO DO TIME', cor: '#5bc0de' },
  { nome: 'APRESENTAÇÃO AO CLIENTE', cor: '#9370db' },
  { nome: 'VALORES', cor: '#ff69b4' },
  { nome: 'ENTREGA AO CLIENTE', cor: '#5cb85c' },
];

const Tarefas = () => {
  return (
    <div className="kanban-container">
      {etapas.map((etapa, index) => (
        <div key={index} className="kanban-col">
          <div className="kanban-header" style={{ backgroundColor: etapa.cor }}>
            {etapa.nome}
          </div>
          <div className="kanban-cards">
            {/* Aqui você pode renderizar os cards */}
          </div>
        </div>
      ))}
    </div>
  );
};
etapas.map(etapa => (
  <div key={etapa.nome} className="kanban-col">
    <div className="kanban-header" style={{ backgroundColor: etapa.cor }}>
      {etapa.nome}
    </div>
    <div className="kanban-cards">
      {quadros.filter(q => q.etapa === etapa.nome).map(q => (
        <div key={q.id} className="kanban-card">
          {q.nome}
        </div>
      ))}
    </div>
  </div>
));

export default Tarefas;
